	public void detenerGrabacion() {
		Thread thread = new Thread() {
			public void run() {
				if (recorder != null)
					recorder.stop();
				recorder.reset(); // You can reuse the object by going back to
				// setAudioSource() step
				recorder.release();
			}
		};
		thread.start();
	}

